        if (vs.empty()) return os << "[]";
        os << "[" << vs[0];
        for (int i = 1; i < vs.size(); i++) os << " " << vs[i];
        return os << "]";
    }
    template<class T> istream& operator>>(istream& is, vector<T>& vs) {
        for (auto it = vs.begin(); it != vs.end(); it++) is >> *it;
        return is;
    }

    bool isVowel(char x) {
        const string s = "aiueo";
        for (int i = 0; i < 5; i++) if (s[i] == x) return true;
        return false;
    }

    int N;
    vector<string> S;
    bool input() {
        cin >> N;
        if (N == 0) return false;
        S.clear(); S.resize(N); cin >> S;
        return true;
    }

    string encode(const string& s) {
        string t;
        t.push_back(s[0]);
        for (int i = 0; i + 1 < s.size(); i++) {
            if (isVowel(s[i])) {
                t.push_back(s[i + 1]);
            }
        }
        return t;
    }

    void solve() {
        vector<string> T(N);
        int K = 0;
        for (int i = 0; i < N; i++) {
            T[i] = encode(S[i]);
            K = max(K, int(T[i].size()));
        }
        int ans = -1;
        for (int k = 1; k <= K; k++) {
            vector<string> ss;
            for (int i = 0; i < N; i++) {
                ss.push_back(T[i].substr(0, min(k, int(T[i].size()))));
            }
            for (int i = 0; i < N; i++) {
                for (int j = i + 1; j < N; j++) {
                    if (ss[i] == ss[j]) {
                        goto next;
                    }
                }
            }
            ans = k;
            break;
            next:;
        }
        cout << ans << endl;
    }
}

int main() {
    while (input()) solve();
    return 0;
}

