#define times(i,n) for(int i=n;i;i--)

struct tree {
	int n;
	unordered_map<char, tree *> c;
	~tree () {
		for (auto i: c)
			delete i.second;
	}
	tree * insert(char a) {
		n++;
		if (!c.count(a))
			c[a] = new tree();
		return c[a];
	}
	int min_code (int d) {
		if (n <= 1)
			return d;
		int r {};
		for (auto i: c)
			r = max(r, i.second->min_code(d + 1));
		return r;
	}
};

