bool Boin(char ipt) {
  if (ipt == 'a' or ipt == 'i' or ipt == 'u' or ipt == 'e' or ipt == 'o') {
    return true;
  } else {
    return false;
  }
}

int Solve(const int n) {
  vector<string> s(n);
  vector<string> res(n, "");

  list<int> ls;
  map<string, int> mp;

  for (auto &&e : s) {
    cin >> e;
  }

  for (int i = 0; i < n; ++i) {
    res[i] += s[i][0];

    for (size_t j = 1; j < s[i].size(); ++j) {
      if (Boin(s[i][j - 1])) {
        res[i] += s[i][j];
      }
    }

    ++mp[res[i]];
    if (mp[res[i]] > 1) {
      return -1;
    }

    ls.push_back(i);
  }

  int k = 0;
  while (!ls.empty()) {
    vector<int> val(26, 0);
    for (auto itr = ls.begin(), last = ls.end(); itr != last; ++itr) {
      ++val[res[*itr][k] - 'a'];
    }
    for (auto itr = ls.begin(); itr != ls.end();) {
      if (k + 1 == res[*itr].size()) {
        itr = ls.erase(itr);
      } else if (val[res[*itr][k] - 'a'] == 1) {
        itr = ls.erase(itr);
      } else {
        ++itr;
      }
    }
    ++k;
  }

  return k;
}

int main() {
  int n;
  while (cin >> n and n != 0) {
    cout << Solve(n) << endl;
  }
}

