using namespace std;

bool isvowel(char c) {
	if (c == 'a' || c == 'i' || c == 'u' || c == 'e' || c == 'o') return true;
	return false;
}

string tocode(string s) {
	const int N = s.size();
	string res;
	res += s[0];
	for (int i = 0; i < N; ++i)
		if (i < N - 1 && isvowel(s[i])) res += s[i + 1];
	return res;
}

int match(string s1, string s2) {
	const int N = min(s1.size(), s2.size());
	int res = 0;
	for (int i = 0; i < N; ++i) {
		if (s1[i] == s2[i]) res++;
		else break;
	}
	return res;
}

int main() {
	int n;
	while (cin >> n, n) {
		vector<string> s(n);
		for (int i = 0; i < n; ++i) {
			cin >> s[i];
			s[i] = tocode(s[i]);
		}
		sort(s.begin(), s.end());
		int ans = 0;
		for (int i = 1; i < n; ++i) {
			if (s[i - 1] == s[i]) {
				ans = -1;
				break;
			}
			ans = max(ans, match(s[i - 1], s[i]) + 1);
		}
		cout << ans << endl;
	}

	return 0;
}

