            used.clear();

            for(i = 0; i < n; ++i) {
                s = std::string(code[i].begin(), code[i].begin() + std::min(k, (int)code[i].size()));
                used.insert(s);
            }
            if(used.size() == n) {
                ans = k;
                break;
            }
        }
        std::cout << ans << std::endl;
