	for(int iz = 0;iz < n;iz++){
		string tmp = "";
		string input;

		cin >> input;
		tmp.push_back(input[0]);

		for(string::iterator i = input.begin(); i != input.end()-1; i++ ){
			if(judge(*i))
				tmp.push_back(*(i+1));
		}
		table.push_back(tmp);
	}

	//resize 
	/*
	for(vector<string>::iterator i = table.begin(); i != table.end(); i++){
