	/*cout << "===" << endl;
	for(auto i = newtable.begin(); i != newtable.end(); i++){
		cout << *i << endl;
	}
	cout << "===" << endl;*/

	//compare
	int k = 0;
	for(int i  = 1; i <= 50; i++){
		set<string> dup;
		bool flag = true;
		for(auto it = newtable.begin(); it != newtable.end(); it++){
			int index = i;
			if (it->length() <= i)
				index = it->length();
			if(dup.count(string(it->begin(), it->begin()+index)) >= 1){
				flag = false;
				break;
			}
			dup.insert(string(it->begin(), it->begin()+index));
		}
