string part(string str, unsigned int num)
{
	string ret = str.substr(0, 1);
	int count = 0;

	for (unsigned int i = 0; i < str.size() - 1; i++) {
		if (str[i] == 'a' || str[i] == 'i' || str[i] == 'u' || str[i] == 'e' || str[i] == 'o') {
			ret += str.substr(i + 1, 1);
		}
	}

	if (ret.size() > num) {
		ret = ret.substr(0, num);
	}

	return ret;
}

int solve(vector<string>& strings, unsigned int num)
{
	int ans = -1;
	bool flag = true;
	vector<string> parts;

	for (unsigned int i = 0; i < strings.size(); i++) {
		string temp = part(strings[i], num);
		parts.push_back(temp);
	}

	for (unsigned int i = 0; i < parts.size(); i++) {
		for (unsigned int j = i + 1; j < parts.size(); j++) {
			if (parts[i] == parts[j]) {
				flag = false;
				goto End;
			}
		}
	}
End:

	if (flag == true) {
		ans = num;
	}

	return ans;
}

