    while (cin>>n) {
        if (n==0) {
            break;
        } else {
            std::deque<string> deq;
            int max_size=0;
            string tmp;
            for (int i=0;i<n;i++) {
                cin>>s;
                tmp="";
                for (int j=0;j<s.size();j++) {
                    if (j==0) {
                        tmp+=s.at(j);
                    }
                    if ( (s.at(j)=='a'||s.at(j)=='i'||s.at(j)=='u'||s.at(j)=='e'||s.at(j)=='o')&&j!=s.size()-1) {
                        tmp+=s.at(j+1);
                    }
                }
                deq.emplace_back(tmp);
                if (max_size<tmp.size()) {
                    max_size=tmp.size();
                }
            }
            int bad_count=0;
            for (int k=1;k<=max_size;k++) {
                std::deque<string> code;
                int count=0;
                for (int l=0;l<deq.size();l++) {
                    if (deq[l].size()<k) {
                        if (find(code.begin(),code.end(),deq[l])==code.end()) {
                            code.emplace_back(deq[l]);
                            count++;
                        } else {
                            break;
                        }
                    } else {
                        if (find(code.begin(),code.end(),deq[l].substr(0,k))==code.end()) {
                            code.emplace_back(deq[l].substr(0,k));
                            count++;
                        } else {
                            break;
                        }
                    }
                }
                if (count==deq.size()) {
                    cout<<k<<endl;
                    break;
                } else {
                    bad_count++;
                }
            }
            if (bad_count==max_size) {
                cout<<-1<<endl;
            }
        }
    }
}

