std::string compress(std::string const & name) {
  std::string const vowels("aiueo");
  std::string val;
  val.push_back(name[0]);
  for (int i = 1, len = name.length(); i < len; ++i) {
    if (vowels.find(name[i-1]) != std::string::npos) {
      val.push_back(name[i]);
    }
  }
  return val;
}

struct Impossible {};

int mindiff(std::string const & x, std::string const & y) {
  int const limit = std::min(x.length(), y.length());
  for (int i = 0; i < limit; ++i) {
    if (x[i] != y[i]) {
      return i + 1;
    }
  }
  return (x.length() == y.length() ? (throw Impossible()) : limit + 1);
}

int main() {
  int N;
  while (std::cin >> N && N) {
    std::vector<std::string> airports;
    for (int i = 0; i < N; ++i) {
      std::string S;
      std::cin >> S;
      airports.push_back(compress(S));
    }
    std::sort(airports.begin(), airports.end());
    try {
      std::vector<int> diffs;
      for (int i = 1; i < N; ++i) {
        diffs.push_back(mindiff(airports[i - 1], airports[i]));
      }
      std::cout << std::accumulate(
          diffs.begin(),
          diffs.end(),
          1,
          [](int x, int y) {
            return std::max(x, y);
          })
        << std::endl;
    } catch (Impossible const & e) {
      std::cout << "-1" << std::endl;
    }
  }
  return 0;
}

