int main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout << fixed << setprecision(10);
    while(true){
        int n;
        cin >> n;
        if(n == 0) break;
        vector<string> s(n);
        for(int i = 0; i < n; ++i){
            cin >> s[i];
        }
        vector<string> t(n);
        int k_max = 0;
        for(int i = 0; i < n; ++i){
            t[i].push_back(s[i][0]);
            for(int j = 1; j < s[i].size(); ++j){
                if(is_boin(s[i][j-1])) t[i].push_back(s[i][j]);
            }
            k_max = max(k_max, (int)t[i].size());
        }
        int ans = -1;
        for(int k = 1; k <= k_max; ++k){
            for(int i = 0; i < n; ++i){
                for(int j = i + 1; j < n; ++j){
                    if(t[i].size() != t[j].size() && (t[i].size() < k || t[j].size() < k)) continue;
                    bool flg = true;
                    for(int l = 0; l < min({k, (int)t[i].size(), (int)t[j].size()}); ++l){
                        flg &= t[i][l] == t[j][l];
                    }
                    if(flg) goto NG;
                }
            }
            ans = k;
            break;
            NG:;
        }
        cout << ans << "\n";
    }
    return 0;
}

