			code += temp[0];
			for (i = 0; i < temp.size(); i++){
				if (temp[i] == 'a' || temp[i] == 'i' || temp[i] == 'u' || temp[i] == 'e' || temp[i] == 'o'){
					code += temp[i + 1];
				}
			}
			Code.push_back(code);
			if (k == 0) max = code.size();
			else if (code.size() > max) max = code.size();
		}
		for (k = 0;k < max; k++){
			for (i = 0; i < n - 1; i++){
				for (j = i + 1; j < n; j++){
					if (k >= Code[i].size() && k >= Code[j].size()){
						if (Code[i] == Code[j]){ k = max; i = n - 1; j = n; break; }
