string func(string s, int k) {
  string ret = "";
  ret = ret + s[0];
  for(int i = 0; i < s.size() - 1; i++) {
    if(s[i] == 'a' or
       s[i] == 'i' or 
       s[i] == 'u' or 
       s[i] == 'e' or 
       s[i] == 'o') {
        ret = ret + s[i + 1];
    }
  }

  if(ret.size() < k) return ret;

  string rett = "";
  for(int i = 0; i < k; i++) rett = rett + ret[i];
  return rett;

}

int main(){

  while(1) {

    int n; cin >> n;
    if(n == 0) break;
    vector<string> v(n);
    for(int i = 0; i < n; i++) cin >> v[i];
    int ans = -1;
    for(int k = 1; k < 53; k++) {
      vector<string> zip(n);
      for(int i = 0; i < n; i++) zip[i] = func(v[i], k);

      bool ok = true;
      for(int i = 0; i < n; i++) for(int j = i + 1; j < n; j++) {
        if(zip[i] == zip[j]) {
          ok = false;
          break;
        }
      }

      if(ok) {
        ans = k;
        break;
      }
    }

    cout << ans << endl;
  }
  return 0;
}

