bool strCmp(char* base, char* comp){
	int length1 = 0,length2 = 0;
	for(int i=0;base[i] != '\0'; i++)length1++;
	for(int i=0;comp[i] != '\0'; i++)length2++;
	if(length1 != length2)return false;

	for(int i=0;base[i] != '\0'; i++){
		if(base[i] != comp[i])return false;
	}
	return true;
}

void strcpy(char* to,char* str){
	for(int i=0;str[i] != '\0';i++){
		to[i] = str[i];
		to[i+1] = '\0';
	}
}

bool calc(int K){

	int calc_index,count = 0;
	char tmp[51];

	for(int i = 0; i < N; i++){
		tmp[0] = table[i][0];
		tmp[1] = '\0';
		calc_index = 1;
		for(int p = 1; table[i][p] != '\0' && calc_index < K;p++){
			if(table[i][p-1] == 'a' || table[i][p-1] == 'i' || table[i][p-1] == 'u' || table[i][p-1] == 'e' || table[i][p-1] == 'o'){ //???????????????????????§????????´???
				tmp[calc_index++] = table[i][p];
				tmp[calc_index] = '\0';
			}
		}
		for(int a = 0; a < i; a++){
			if(strCmp(work[a],tmp))return false;
		}
		strcpy(work[i],tmp);
		count++;
	}

	return count == N;
}


