				}
			}
			Code.push_back(code);
			if (k == 0) max = code.size();
			else if (code.size() > max) max = code.size();
		}
		for (k = 0;k < max; k++){
			for (i = 0; i < n - 1; i++){
				for (j = i + 1; j < n; j++){
					if (k >= Code[i].size() && k >= Code[j].size()){
						if (Code[i] == Code[j]){ k = max; break; }
					}
					if (k >= Code[i].size() || k >= Code[j].size()) continue;
					if (Code[i][k] == Code[j][k]) break;
				}
				if (j < n - 1) break;
			}
			if (i == n - 1 && j == n) break;
		}
		if(k==max) cout << -1 << endl;
		else cout << k + 1 << endl;
	}
	return 0;
}

