string getCode(string s, int k) {
  string ans;
  ans = s[0];
  int len = s.size() - 1;
  for(int i = 0; i < len; i++) {
    if(isvowel(s[i])) {
      ans += s[i+1];
    }
  }
  if((int)ans.size() < k)
    return ans;
  return ans.substr(0, k);
}

int code_length(int n) {
  bool flag;

  for(int k = 1; k < 51; k++) {
    flag = true;
    map<string, bool> mp;
    for(int i = 0; i < n; i++) {
      string s = getCode(name[i], k);
      if(mp.count(s) == 0) {
        mp[s] = true;
      }
      else {
        flag = false;
        break;
      }
    }
    if(flag)
      return k;
  }
  return -1;
}

