					case 'u':
					case 'e':
					case 'o':
						t[i].push_back(s[i][j + 1]);
						break;
					}
					if (t[i].size() >= k) {
						break;;
					}
				}
			}
			sort(t.begin(), t.end());
			FOR(i, 0, n - 2) {
				if (t[i] == t[i + 1]) {
					break;
				}
				if (i == n - 2) {
					result = k;
				}
			}
			if (result != -1) {
				break;
			}
		}

		cout << result << endl;
	}

	return 0;
}

