string proc(string buf,int k){
    string ou;
    for(auto it=buf.begin();it!=buf.end()&&ou.size()<k;it++){
        if(it==buf.begin()){
            ou.push_back(*it);
        }
        else{
            if(*(it-1)=='a' || *(it-1)=='i' || *(it-1)=='u' || *(it-1)=='e' || *(it-1)=='o'){ou.push_back(*it);}
        }
    }
    return ou;
}

