            sz=MAX(sz,buf.size());
            rd.push_back(buf);
        }
        k=0;
        while(k<=sz){
            k++;
            prc.clear();
            for(auto it=rd.begin();it!=rd.end();it++){
                p=proc(*it,k);
                auto ps=find(prc.begin(),prc.end(),p);
                if(ps!=prc.end()){break;}
