#include<bits/stdc++.h>
using namespace std;

string makeCode(string &s, int k){
  set<char> vowel = {'a', 'i', 'u', 'e', 'o'};
  string res = s.substr(0, 1);
  for (int i = 0; i < s.length() - 1; i++) {
    if(vowel.find(s[i]) == vowel.end())continue;
    res += s[i + 1];
  }
  return res.substr(0, k);
}

