    while(f and k <= mini){
      k++;
      f = false;
      set<string> ss;
      for (int i = 0; i < n; i++) {
        string code = makeCode(s[i], k);
        //std::cout << code << std::endl;
        if(ss.find(code) != ss.end())f = true;
        ss.insert(code);
      }
    }
    if(k <= mini)std::cout << k << std::endl;
    else std::cout << -1 << std::endl;
  }
  return 0;
}

