double px[200],py[200],pz[200],r[200],vx[200],vy[200],vz[200],vr[200],vt[200];
priority_queue<pal, vector<pal>, greater<pal> > pq;
double eps=0.000000001;
void cul(int i,int j){

	double a[3],b[3];
	a[0]=px[i]-px[j];
	a[1]=py[i]-py[j];
	a[2]=pz[i]-pz[j];
	b[0]=vx[i]-vx[j];
	b[1]=vy[i]-vy[j];
	b[2]=vz[i]-vz[j];
	double e=0.0,f=0.0,g=0.0,a1,a2,a3;
	for(int i=0;i<3;i++){
		g += a[i]*a[i];
		f += 2*a[i]*b[i];
		e += b[i]*b[i];
	}

	double d=vr[i]+vr[j],c=r[i]+r[j];
	a1=e-d*d;
	a2=f+2*d*c;
	a3=g-c*c;
	//if(i==0&&j==2)	cout<<e<<" "<<f<<" "<<g<<endl;
	if(abs(a1)<eps && abs(a2)<eps) return ;
	if(abs(a1)<eps){
		if(-a3/a2>eps){
			double ty=-a3/a2;
		if(ty<vt[i] && ty <vt[j] && ty>eps)	pq.push(mp(ty,mp(i,j)));
		}
		return;
	}
	if(a2*a2-4.0*a1*a3<=eps) return;
	double q1,q2;
	q1=(-a2-sqrt(a2*a2-4.0*a1*a3))/(2.0*a1);
	q2=(-a2+sqrt(a2*a2-4.0*a1*a3))/(2.0*a1);
	if(q1>q2) swap(q1,q2);

	if(q2<eps) return;

	if(q1<eps){
	if(q2<vt[i] && q2<vt[j] && q2>eps)	pq.push(mp(q2,mp(i,j)));
	}
	else {
	if(q1<vt[i] && q1<vt[j] &&q1>eps)	pq.push(mp(q1,mp(i,j)));
	}
	return ;
}

