set<char> mom{'a', 'i', 'u', 'e', 'o'};

void solve(int n) {
    for (int i = 0; i < n; i++) {
        scanf("%s", strbuf);
        s[i] = strbuf;
    }
    set<string> se;
    for (int i = 0; i < n; i++) {
        const int L = s[i].length();
        string code = "";
        code += s[i][0];
        for (int j = 0; j < L - 1; j++) {
            if (mom.count(s[i][j])) code += s[i][j + 1];
        }
        if (se.count(code)) {
            puts("-1");
            return;
        }
        se.insert(code);
    }
    using state = pair<int, string>;//(length, word)
    priority_queue<state> pq;
    for (string e : se) pq.push(make_pair(e.length(), e));
    int k = INF;
    while (!pq.empty()) {
        vector<string> vec;
        vec.emplace_back(pq.top().second);
        const int L = vec.front().length();
        pq.pop();
        while (!pq.empty() && pq.top().first == L) {
            vec.emplace_back(pq.top().second);
            pq.pop();
        }
        chmin(k, L);
        for (string &e : vec) {
            se.erase(e);
            e.pop_back();
            if (se.count(e)) {
                printf("%d\n", k);
                return;
            }
            se.insert(e);
        }
        for (string &e : vec) {
            pq.push(make_pair(e.length(), e));
        }
    }
    puts("1");
}

