class UnionFind {
public:
    vector <int> par; 
    vector <int> siz; 

    UnionFind(int sz_): par(sz_), siz(sz_, 1) {
        for (ll i = 0; i < sz_; ++i) par[i] = i;
    }
    void init(int sz_) {
        par.resize(sz_);
        siz.assign(sz_, 1LL);
        for (ll i = 0; i < sz_; ++i) par[i] = i;
    }

    int root(int x) { 
        while (par[x] != x) {
            x = par[x] = par[par[x]];
        }
        return x;
    }

    bool merge(int x, int y) {
        x = root(x);
        y = root(y);
        if (x == y) return false;
        if (siz[x] < siz[y]) swap(x, y);
        siz[x] += siz[y];
        par[y] = x;
        return true;
    }

    bool issame(int x, int y) { 
        return root(x) == root(y);
    }

    int size(int x) { 
        return siz[root(x)];
    }
};

class WeightedUnionFind{
public:
    vector <int> par; 
    vector <int> siz;
    vector <ll> diff_weight; /* 頂点間の重みの差 */

    WeightedUnionFind(int sz_): par(sz_), siz(sz_, 1LL), diff_weight(sz_, 0LL){
        for(int i=0; i<sz_; i++) par[i] = i;
    }

    void init(int sz_){
        par.resize(sz_);
        siz.assign(sz_, 1LL);
        diff_weight.resize(sz_);
        for(int i=0; i<sz_; i++) par[i] = i, diff_weight[i] = 0;
    }

    int root(int x){
        if(par[x] == x){
            return x;
        }else{
            int r = root(par[x]);
            diff_weight[x] += diff_weight[par[x]];
            return par[x] = r;
        }
    }

    ll weight(ll x){
        root(x);
        return diff_weight[x];
    }

    bool issame(int x, int y){
        return root(x) == root(y);
    }

    bool merge(int x, int y, ll w){
        w += weight(x); w -= weight(y);
        x = root(x); y = root(y);
        if(x == y) return false;

        if (siz[x] < siz[y]) swap(x, y), w = -w;
        siz[x] += siz[y];
        par[y] = x;
        diff_weight[y] = w;
        return true;
    }

    ll diff(int x, int y){
        return weight(y) - weight(x);
    }

};

ll modPow(ll x, ll n, ll mod = MOD){
    if(n <= 0) return 1;
    ll res = 1;
    while(n){
        if(n&1) res = (res * x)%mod;

        res %= mod;
        x = x * x %mod;
        n >>= 1;
    }
    return res;
}

