string process(string s) {
  string ret = "";
  ret += s[0];
  int idx = 0;
  bool flag = false;
  while(idx < s.size()) {
    char c = s[idx];
    if (flag) {
      ret += c;
      flag = false;
    }
    if (c == 'a'
        || c == 'i'
        || c == 'u'
        || c == 'e'
        || c == 'o') {
      flag = true;
    }

    idx++;
  }
  return ret;
}

