using namespace std;

inline bool IsBoin(const char c) {
    return c == 'a' || c == 'i' || c == 'u' || c == 'e' || c == 'o';
}

int MaxK(const string s1, const string s2) {
    const int size = min(s1.size(), s2.size());
    for (int i = 0; i < size; ++i) {
        if (s1[i] != s2[i]) return i + 1;
    }
    return size + 1;
}

int Solve(const int n, vector<string> &s) {
    for (int i = 0; i < n; ++i) {
        const int size = s[i].size();
        string new_s(1, s[i][0]);

        for (int j = 1; j < size; ++j)
            if (IsBoin(s[i][j - 1])) new_s.push_back(s[i][j]);

        s[i] = new_s;
    }

    sort(s.begin(), s.end());

    int max_k = 0;
    for (int i = 0; i + 1 < n; ++i) {
        if (s[i] == s[i + 1]) return -1;
        max_k = max(max_k, MaxK(s[i], s[i + 1]));
    }

    return max_k;
}

