using namespace std;

struct Trie {
	Trie *next[26];
	vector<int>cnt;

	Trie() {
		fill(next, next + 26, (Trie *)0);
		cnt.resize(27);
	}

	void insert(const char *s) {
		//cerr << *s << endl;
		if (*s == '\0') {
			cnt[26]++;
			return;
		}
		else cnt[*s - 'a']++;
		if (this->next[*s-'a'] == NULL)
			this->next[*s-'a'] = new Trie();
		this->next[*s-'a']->insert(s + 1);
	}

	bool find(const char *s) {
		if (*s == '\0') return true;
		if (this->next[*s] == NULL)
			return false;
		return this->next[*s]->find(s + 1);
	}
};

int solve(Trie* trie, int depth) {
	int maxDepth = depth;
	bool equal = true;
	for (int i = 0; i < 26; i++) {
		if (trie->cnt[i] >= 2) {
			maxDepth = max(maxDepth, solve(trie->next[i], depth + 1));
		}
		if (trie->cnt[i] != 0)equal = false;
	}
	if (trie->cnt[26] >= 2)equal = true;

	if (equal)return 100;

	return maxDepth;
}

int main()
{
	while (1) {
		int n; cin >> n;
		if (n == 0)break;
		Trie* trie = new Trie();
		for (int i = 0; i < n; i++) {
			string s; cin >> s;
			string code;
			code.push_back(s[0]);
			bool flag = false;
			for (int j = 0; j < s.size(); j++) {
				if (flag)code.push_back(s[j]);
				if (s[j] == 'a' || s[j] == 'i' || s[j] == 'u' || s[j] == 'e' || s[j] == 'o')flag = true;
				else flag = false;
			}

			trie->insert(code.data());
		}

		int ans = solve(trie, 1);
		if (ans == 100)cout << -1 << endl;
		else cout << ans << endl;
	}
    return 0;
}

