string airport_code(string s) {
    string ret = "";
    ret += s[0];
    for (int i = 1; i < s.size(); i++) {
        for (auto c : "aiueo") {
            if (s[i - 1] == c) ret += s[i];
        }
    }
    return ret;
}

bool check_duplicate(vector<string> &codes, int k) {
    set<string> s;
    for (auto code : codes) {
        if (code.size() < k)
            s.insert(code);
        else
            s.insert(code.substr(0, k));
    }
    return (s.size() == codes.size());
}

