string getCode(string s)
{
  static string VOWEL = "aiueo";
  string code = "";
  code += s[0];
  for(int i = 1; i < s.size(); i++) {
    if(VOWEL.find(s[i-1]) != string::npos) code += s[i];
  }
  return code;
}

int identifiableCodeLength(int n, string s[])
{
  int maxLen = -1;
  for(int i = 0; i < n; i++) {
    s[i] = getCode(s[i]);
    maxLen = max(maxLen, (int)s[i].size());
  }
  for(int len = 1; len <= maxLen; len++) {
    set<string> codes;
    for(int i = 0; i < n; i++) {
      string code = s[i].substr(0, min(len, (int)s[i].size()));
      if(codes.find(code) == codes.end()) {
	codes.insert(code);
      } else {
	break;
      }
    }
    if(codes.size() == n) return len;
  }
  return -1;
}

