int solve_testcase() {
    int N; cin >> N;
    if(N == 0) return 1;

    vector<string> vs(N);
    for(int i=0; i<N; i++) cin >> vs[i];

    const int MAX = 52;
    int ub = MAX, lb = -1;
    while(ub - lb > 1) {
        int mid = (ub + lb) / 2;
        if(check(vs, mid)) ub = mid;
        else lb = mid;
    }

    if(ub == MAX) cout << -1 << endl;
    else cout << ub << endl;
    return 0;
}

