}

int n;
char s[64][64];
char code[64][64];

int solve(void) {
	int left = 0, right = 63;
	int answer = -1;
	while (left <= right) {
		int mid = (left + right) / 2;
		int i, j;
		int valid = 1;
		for (i = 0; valid && i < n; i++) {
			for (j = i + 1; j < n; j++) {
				if (memcmp(code[i], code[j], mid) == 0) {
					valid = 0;
					break;
				}
			}
		}
		if (valid) {
			if (answer < 0 || mid < answer) answer = mid;
			right = mid - 1;
		} else {
			left = mid + 1;
		}
	}
	return answer;
}

