import java.util.*;
import java.io.*;
import java.awt.*;
import java.awt.geom.Point2D;
import static java.lang.Math.*;
public class Main {
    public static void main(String[] $) {
        while (true){
            int n=sc.nextInt();
            if(n==0)break;
            String[] name=new String[n];
            for (int i = 0; i < n; i++) {
                name[i]=sc.next();
            }
            int ans=51;
            for (int k = 50; k>0; k--) {
                HashSet<String> set=new HashSet<>();
                for (int i = 0; i < n; i++) {
                    String temp=make(name[i]);
                    set.add(temp.substring(0,min(k,temp.length())));
                }
                //out.println(set);
                if(set.size()==n){
                    ans=k;
                }
            }
            out.println(ans==51?-1:ans);
        }
        out.close();
    }

    static String make(String s){
        char c=s.charAt(0);
        String t=""+c;
        int len=s.length();
        for (int i = 1; i < len; i++) {
            if(c=='a'||c=='i'|c=='u'||c=='e'||c=='o'){
                t+=c=s.charAt(i);
            }else {
                c=s.charAt(i);
            }
        }
        return t;
    }

    static long mod = (long) 1e9 + 7;

    //クラスカル法(0-indexed) n頂点m辺
    static HashSet<Integer> Kruskal(int[] u, int[] v, int[] cost, int n) {
        int m = u.length;
        UF.Init(n);
        PriorityQueue<Integer> S = new PriorityQueue<>((p1, p2) -> cost[p1] - cost[p2]);
        for (int i = 0; i < m; i++) {
            S.add(i);
        }
        //最小全域木をなす辺
        HashSet<Integer> F = new HashSet<>();
        while (!S.isEmpty()) {
            int e = S.poll();
            if (!UF.same(u[e], v[e])) {
                F.add(e);
                UF.unite(u[e], v[e]);
            }
        }
        return F;
    }

    //Union Find木(0-indexed)
    static class UF {
        static int[] d;

        static void Init(int size) {
            d = new int[size];
            for (int i = 0; i < size; i++) {
                d[i] = -1;
            }
        }

        static boolean unite(int x, int y) {
            x = find(x);
            y = find(y);
            if (x == y) return false;
            if (d[x] > d[y]) {
                int t = d[y];
                d[y] = d[x];
                d[x] = t;
            }
            d[x] += d[y];
            d[y] = x;
            return true;
        }

        static boolean same(int x, int y) {
            return find(x) == find(y);
        }

        static int find(int k) {
            return d[k] < 0 ? k : (d[k] = find(d[k]));
        }

        static int size(int k) {
            return -d[find(k)];
        }
    }

    static PrintWriter out = new PrintWriter(System.out);

    static class sc {
        static Scanner s = new Scanner(System.in);

        static String next() {
            return s.next();
        }

        static String nextLine() {
            return s.nextLine();
        }

        static int nextInt() {
            return Integer.parseInt(s.next());
        }

        static long nextLong() {
            return Long.parseLong(s.next());
        }

        static double nextDouble() {
            return Double.parseDouble(s.next());
        }

        static boolean hasNext() {
            return s.hasNext();
        }

        static boolean hasNextInt() {
            return s.hasNextInt();
        }

    }

    static long gcd(long a, long b) {
        return a % b == 0 ? b : gcd(b, a % b);
    }

}

import java.util.*;

public class Main {
    private static final String VOWEL = "aeiou";

    public static void main(String[] args) {
        Scanner scn = new Scanner(System.in);

        int n;
        while((n = scn.nextInt()) > 0) {
            int max = 0;
            List<String> list = new ArrayList<>();
            for(int i = 0; i < n; i++) {
                String str = scn.next();
                max = Math.max(max, str.length());
                list.add(str);
            }

            int ans = 0;
            Set<String> set;
            while(++ans <= max) {
                set = new HashSet<>();
                for(String str : list) {
                    set.add(encode(str, ans));
                }
                if(set.size() == n) {
                    break;
                }
            }

            System.out.println(ans > max ? -1 : ans);
        }
    }

    private static String encode(String str, int len) {
        StringBuilder sb = new StringBuilder();
        boolean flg = true;
        for(char elm : str.toCharArray()) {
            if(flg) {
                sb.append(elm);
                flg = false;
            }
            if(VOWEL.indexOf(elm) >= 0) {
                flg = true;
            }
        }
        return sb.toString().substring(0, Math.min(len, sb.length()));
    }
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.InputMismatchException;
import java.util.NoSuchElementException;

public class Main{

	static PrintWriter out;
	static InputReader ir;

	static void solve(){
		for(;;){
            int n=ir.nextInt();
            if(n==0)
                break;
            char[][] ap=new char[n][];
            String[] cd=new String[n];
            int ma=0;
            for(int i=0;i<n;i++){
            	ap[i]=ir.next().toCharArray();
            	cd[i]=Character.toString(ap[i][0]);
            	for(int j=0;j<ap[i].length-1;j++){
            		if(ap[i][j]=='a'||ap[i][j]=='i'||ap[i][j]=='u'||ap[i][j]=='e'||ap[i][j]=='o'){
            			cd[i]+=Character.toString(ap[i][j+1]);
            		}
            	}
            	ma=Math.max(ma,cd[i].length());
            }
            outer:
            for(int i=0;i<ma;i++){
            	for(int j=0;j<n;j++){
            		for(int k=j+1;k<n;k++){
            			if(code(cd[j],i+1).equals(code(cd[k],i+1))){
            				if(i==ma-1)
            					out.println(-1);
            				continue outer;
            			}
            		}
            	}
            	out.println(i+1);
            	break;
            }
        }
	}

	static String code(String s,int k){
		if(s.length()<=k) return s;
		return s.substring(0,k);
	}

	public static void main(String[] args) throws Exception{
		ir=new InputReader(System.in);
		out=new PrintWriter(System.out);
		solve();
		out.flush();
	}

	static class InputReader {

		private InputStream in;
		private byte[] buffer=new byte[1024];
		private int curbuf;
		private int lenbuf;

		public InputReader(InputStream in) {this.in=in; this.curbuf=this.lenbuf=0;}

		public boolean hasNextByte() {
			if(curbuf>=lenbuf){
				curbuf= 0;
				try{
					lenbuf=in.read(buffer);
				}catch(IOException e) {
					throw new InputMismatchException();
				}
				if(lenbuf<=0) return false;
			}
			return true;
		}

		private int readByte(){if(hasNextByte()) return buffer[curbuf++]; else return -1;}

		private boolean isSpaceChar(int c){return !(c>=33&&c<=126);}

		private void skip(){while(hasNextByte()&&isSpaceChar(buffer[curbuf])) curbuf++;}

		public boolean hasNext(){skip(); return hasNextByte();}

		public String next(){
			if(!hasNext()) throw new NoSuchElementException();
			StringBuilder sb=new StringBuilder();
			int b=readByte();
			while(!isSpaceChar(b)){
				sb.appendCodePoint(b);
				b=readByte();
			}
			return sb.toString();
		}

		public int nextInt() {
			if(!hasNext()) throw new NoSuchElementException();
			int c=readByte();
