class FastScanner {
    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int buflen = 0;
    private boolean hasNextByte() {
        if (ptr < buflen) {
            return true;
        }else{
            ptr = 0;
            try {
                buflen = in.read(buffer);
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (buflen <= 0) {
                return false;
            }
        }
        return true;
    }
    private int readByte() { if (hasNextByte()) return buffer[ptr++]; else return -1;}
    private static boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}
    private void skipUnprintable() { while(hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;}
    public boolean hasNext() { skipUnprintable(); return hasNextByte();}
    public String next() {
        if (!hasNext()) throw new NoSuchElementException();
        StringBuilder sb = new StringBuilder();
        int b = readByte();
        while(isPrintableChar(b)) {
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }
    public long nextLong() {
        if (!hasNext()) throw new NoSuchElementException();
        long n = 0;
        boolean minus = false;
        int b = readByte();
        if (b == '-') {
            minus = true;
            b = readByte();
        }
        if (b < '0' || '9' < b) {
            throw new NumberFormatException();
        }
        while(true){
            if ('0' <= b && b <= '9') {
                n *= 10;
                n += b - '0';
            }else if(b == -1 || !isPrintableChar(b)){
                return minus ? -n : n;
            }else{
                throw new NumberFormatException();
            }
            b = readByte();
        }
    }
    public int nextInt() {
    	if (!hasNext()) throw new NoSuchElementException();
        int n = 0;
        boolean minus = false;
        int b = readByte();
        if (b == '-') {
            minus = true;
            b = readByte();
        }
        if (b < '0' || '9' < b) {
            throw new NumberFormatException();
        }
        while(true){
            if ('0' <= b && b <= '9') {
                n *= 10;
                n += b - '0';
            }else if(b == -1 || !isPrintableChar(b)){
                return minus ? -n : n;
            }else{
                throw new NumberFormatException();
            }
            b = readByte();
        }
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.HashSet;
import java.util.Set;

public class Main {
	private static final String VOWEL = "aeiou";

	public static void main(String args[]) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		while (true) {
			int n = Integer.parseInt(br.readLine());
			if (n == 0)
				break;

			int max = 0;

			ArrayList<String> list = new ArrayList<>();
			for (int i = 0; i < n; i++) {
				String str = br.readLine();
				max = Math.max(str.length(), max);
				list.add(str);
			}
			int ans = 0;
			Set<String> set;
			while (++ans <= max) {
				set = new HashSet<>();
				for (String str : list) {
					set.add(trim(str, ans));
				}
				if (set.size() == n) {
					break;
				}
			}

			System.out.println(ans > max ? -1 : ans);
		}
	}

	static String trim(String str, int len) {
		StringBuilder sb = new StringBuilder();
		boolean flg = true;
		for (char elm : str.toCharArray()) {
			if (flg) {
				sb.append(elm);
				flg = false;
			}
			if (VOWEL.indexOf(elm) >= 0) {
				flg = true;
			}
		}
		return sb.toString().substring(0, Math.min(len, sb.length()));
	}
}

import java.util.*;

class Main{
  static Set<Character> list;
  public static void main(String[] args) {
    Scanner scan = new Scanner(System.in);
    list = new HashSet<>();
    list.add('a');
    list.add('i');
    list.add('u');
    list.add('e');
    list.add('o');
    int n;
    loop:while(scan.hasNext()) {
      n = scan.nextInt();
      if(n==0) return;
      String[] arr = new String[n];
      for(int i = 0; i < n; i++) arr[i] = getShortName(scan.next().toCharArray());
      Arrays.sort(arr);
      int result = 0;
      for(int i = 0; i < n-1; i++) {
        if(arr[i].equals(arr[i+1])) {
          System.out.println(-1);
          continue loop;
        }else {
          int same = 0;
          for(int j = 0; j < Math.min(arr[i].length(),arr[i+1].length()); j++) {
            if(arr[i].charAt(j)==arr[i+1].charAt(j)) {
              same++;
            }
          }
          result = Math.max(same,result);
        }
      }
      System.out.println(result+1);
    }
