using System;

public class Hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            var ap = new string[n];
            var maxL = 0;
            for (int i = 0; i < n; i++)
            {
                var s = Console.ReadLine().Trim();
                ap[i] = s;
                maxL = Max(maxL, s.Length);
            }
            Console.WriteLine(getAns(ap,maxL));
        }
    }
    static int getAns (string[] ap, int maxL)
    {
        if (checkK(ap, 1)) return 1;
        if (!checkK(ap, maxL)) return -1;
        var ng = 1; var ok = maxL; 
        while (ok - ng > 1)
        {
            var mid = ng + (ok - ng) / 2;
            if (checkK(ap, mid)) ok = mid;
            else ng = mid;
        }
        return ok;
    }
    static bool checkK (string[] ap , int k)
    {
        var hs = new HashSet<string>();
        foreach (var x in ap)
            if (!hs.Add(getCode(x, k))) return false;
        return true;
    }
    static string getCode (string s , int k)
    {
        var b = "aiueo";
        var ans = s.Substring(0, 1);
        var preb = false;
        if (b.Contains(ans)) preb = true;
        var count = 1;
        var sL = s.Length;
        for (int i = 1; i < sL; i++)
        {
            if (count == k) return ans;
            if (preb) { ans += s[i]; count++; }
            preb = b.Contains(s[i]) ? true : false;
        }
        return ans;
    }
}

using System.Linq;
using System;

public class hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            var code = new string[n];
            for (int i = 0; i < n; i++)
            {
                var s = Console.ReadLine().Trim();
                code[i] = GetName(s);
            }
            PadNN(code);
            var ans = GetL(code);
            if (ans == -1) Console.WriteLine(-1);
            else Console.WriteLine(ans + 1);
        }
    }
    public static void PadNN(string[] c)
    {
        var maxL = c.OrderByDescending(x => x.Length).First().Length;
        for (int i = 0; i < c.Length; i++)
        {
            var n = maxL - c[i].Length;
            c[i] += new string('N', n);
        }
    }
    public static int GetL(string[] c)
    {
        for (int i = 0; i < c[0].Length; i++)
        {
            var same = false;
            for (int j = 0; j < c.Length; j++)
                for (int k = j + 1; k < c.Length; k++)
                    if (c[j][i] == c[k][i]) same = true;
            if (!same) return i;
            }
        return -1;
    }
    public static string GetName(string s)
    {
        var b = new char[] { 'a', 'i', 'u', 'e', 'o' };
        var ans = "";
        var getnow = 1;
        for (int i = 0; i < s.Length; i++)
        {
            if (getnow == 1) ans += s[i].ToString();
            if (Boin(b, s[i])) getnow = 1;
            else getnow = 0;
        }
        return ans;
    }
    public static bool Boin(char[] b, char a)
    {
        foreach (var x in b)
            if (a == x) return true;
        return false;
    }
}

