namespace airport_code
{
    class AirportCode 
    {
        private static string EditName(string name, int k)
        {
            var nameChar = name.ToCharArray();
            var vowels = new List<char>() { 'a', 'i', 'u', 'e', 'o' };
            string editName = nameChar[0].ToString();

            for(int i= 1; i < nameChar.Length && editName.Length < k; i++)
            {
                if (vowels.Contains(nameChar[i - 1]))
                    editName += nameChar[i];
            }

            return editName;

        }

        private static bool notOptimalNames(string[] portNames)
        {
            var distinctPort = portNames.Distinct().ToArray();
            if (distinctPort.SequenceEqual(portNames))
                return false;
            else
                return true;
        }

        private static int GetOpNum(string[] portNames)
        {
            int longestLg = portNames.Select(name => name.Length).Max();
            var editNames = new string[portNames.Length];
            int l = 0;

            while (notOptimalNames(editNames))
            {
                l++;
                if (l > longestLg)
                    return -1;

                editNames = portNames.Select(name => EditName(name, l)).ToArray();
            }

            return l;
        }
        public static void Main(string[] args)
        {
