	let mut sc = Scanner::new(cin);
    loop{
        let n : usize = sc.read();
        if n == 0 { break; }
        let mut names = Vec::new();
        let mut msize = 0;
        for _ in 0..n {
            names.push(sc.read::<String>());
            if msize < names.last().unwrap().len(){
                msize = names.last().unwrap().len();
            }
            //println!("{}", names.last().unwrap());
        }
        //println!("s:{}", msize);
        let mut ended = false;
        for k in 1..msize {
            let mut nms = BTreeSet::new();
            for name in &names {
                if !nms.insert(into_code(name, k)) {
                    break;
                }
            }
            if nms.len() == n {
                //for i in nms {
                    //println!("{:?}", i);
                //}
                println!("{}", k);
                ended = true;
                break;
            }
        }
        if !ended {
            let mut nms = BTreeSet::new();
            for name in &names {
