using namespace std;
typedef unsigned long long ull;
const ull B = 1777771;
ull PB[100001];
ull S[100001];
string str;

ull get(int l,int r){return S[r]-S[l]*PB[r-l];}
ull get(int l,int r,int x,char ch){
  ull res=get(l,r);
  if(x<l||r<=x) return res;
  res-=str[x]*PB[r-x-1];
  res+=ch*PB[r-x-1];
  return res;
}


int search(int a,int b,int x,int ch){
  int L=0,M,R=str.size();
  while(L<R){
    M = (L+R)/2;
    if(get(a,a+M,x,ch)!=get(b,b+M,x,ch)) R = M;
    else L = M+1;
  }
  return L;
}


bool check(int l,int r,int t){
  int x = min(r,l+t+search(l,l+t,-1,0));
  if(--x==r)return 0;
  return min(r+1,l+t+search(l,l+t,x,str[l+(x-l-t)%t])) == r+1;
}

