Hash substr(int l,int r){
	return h[r] - h[l] * p[r-l];
}
Hash concat(Hash a,Hash b,int blen){
	return a * p[blen] + b;
}

Hash makestr(int l,int r,int t){
	int len = r - l;
	int Q = len / t;
	int R = len % t;
	Hash res = 0;

	if( l + t <= r ){

		Hash x = substr(l,l+t);
		int xlen = t;

		for(int i = 0 ; xlen <= len ; i++){
			if( Q >> i & 1 ){
				res = concat(res,x,xlen);
			}

			x = concat(x,x,xlen);
			xlen *= 2;
		}
	}

	res = concat(res,substr(l,l+R),R);
	//cout << res << endl;
	return res;	
}


Hash makestr2(int l,int r,int t){
	int len = r - l;
	int Q = len / t;
	int R = len % t;
	int xlen = t;
	Hash x = substr(l+t,l+2*t);
	Hash res = 0;
	for(int i = 0 ; xlen <= len ; i++){
		if( Q >> i & 1 ){
			res = concat(res,x,xlen);
		}

		x = concat(x,x,xlen);
		xlen *= 2;
	}

	res = concat(res,substr(l,l+R),R);
	//cout << res << endl;
	return res;	
}


