				if (rh.match(ll, ll + p, rl, rl + p)) lb = p;
				else ub = p;
			}
			if (check(ll, r - t, rl, r, p, rh)) ans = true;
			else ans = false;
		}
		cout << (ans ? "Yes" : "No") << endl;
	}
	return 0;
}

