const LL mod[] = { 999999937, 1000000007 }, base = 10000103;

struct RollingHash
{
	int n;
	vector<LL> hs[2], pw[2];
	RollingHash() {};
	RollingHash(const string& s)
	{
		n = s.size();
		REP(i, 2)
		{
			hs[i].assign(n + 1, 0);
			pw[i].assign(n + 1, 0);
			hs[i][0] = 0;
			pw[i][0] = 1;
			REP(j, n)
			{
				pw[i][j + 1] = pw[i][j] * base%mod[i];
				hs[i][j + 1] = (hs[i][j] * base + s[j]) % mod[i];
			}
		}
	}

	LL hash(int l, int r, int i)
	{
		return ((hs[i][r] - hs[i][l] * pw[i][r - l]) % mod[i] + mod[i]) % mod[i];
	}

	bool match(int l1, int r1, int l2, int r2)
	{
		return (hash(l1, r1, 0) == hash(l2, r2, 0)) && (hash(l1, r1, 1) == hash(l2, r2, 1));
	}
};

string s;

bool check(int ll, int lr, int rl, int rr, int p, RollingHash& rh)
{
	int len = lr - ll;
	LL lh0 = rh.hash(ll, lr + 1, 0), rh0 = rh.hash(rl, rr + 1, 0);
	LL lh1 = rh.hash(ll, lr + 1, 1), rh1 = rh.hash(rl, rr + 1, 1);

 	if (ll + p < rl || rl + p > lr)
	{
		lh0 = (lh0 + mod[0] - s[ll + p] * rh.pw[0][len - p] % mod[0]) % mod[0];
		lh1 = (lh1 + mod[1] - s[ll + p] * rh.pw[1][len - p] % mod[1]) % mod[1];
		rh0 = (rh0 + mod[0] - s[rl + p] * rh.pw[0][len - p] % mod[0]) % mod[0];
		rh1 = (rh1 + mod[1] - s[rl + p] * rh.pw[1][len - p] % mod[1]) % mod[1];
	}
	else
	{
		int tp = p + ll;
		lh0 = ((lh0 + s[rl + tp - ll] * rh.pw[0][lr - tp] % mod[0]) % mod[0] - s[tp] * rh.pw[0][lr - tp] % mod[0]) % mod[0];
		lh1 = ((lh1 + s[rl + tp - ll] * rh.pw[1][lr - tp] % mod[1]) % mod[1] - s[tp] * rh.pw[1][lr - tp] % mod[1]) % mod[1];
		rh0 = ((rh0 + s[ll + tp - rl] * rh.pw[0][rr - tp] % mod[0]) % mod[0] - s[tp] * rh.pw[0][rr - tp] % mod[0]) % mod[0];
		rh1 = ((rh1 + s[ll + tp - rl] * rh.pw[1][rr - tp] % mod[1]) % mod[1] - s[tp] * rh.pw[1][rr - tp] % mod[1]) % mod[1];
	}
	return lh0 == rh0 && lh1 == rh1;
}


