ha getha(const int l, const int r) {
	if (changeind != -1) {
		return rh.get_changed_hash(changeind, st[changeind], changeto, l, r);
	}
	else {
		ha h = rh.hash(l, r);
		return h;
	}
}
int L, R, T;
pair<bool, int> issame(int a, int b, int alen) {
	if (b > R) {
		return make_pair(true, -1);
	}
	else {
		assert(b <= R);
		int len = min(alen, R - b);
		bool same = getha(a, a + len) == getha(b, b + len);
		if (same)return make_pair(true, -1);
		else {

			int acheck = check(a,b,len);
			return make_pair(false, acheck);
		}
	}
}
int main() {
	cin >> st;
	rh.init(st);
	int N; cin >> N;
	while (N--) {
		changeind = -1;
		changeto = -1; cin >> L >> R >> T;
		L--; R--;
		int asize = R - L + 1;
		int nt = T;
		while (asize * 2 >= nt) {
			nt *= 2;
		}
		bool ok = true;
		while (nt != T) {
			nt /= 2;
			if (nt >= asize)continue;
			auto ap = issame(L, L + nt, nt-1);
			if (ap.first)continue;
			else {
				int p(ap.second);
				if (p == -1) {
					ok = false;
					break;
				}
				else {
					if (changeind != -1) {
						ok = false;
						break;
					}
					else if (nt == T) {
						ok = true;
						break;
					}
					else {
						bool lsame = issame(L,L+nt/2,nt/2-1).first;
						bool rsame= issame( L + nt,L+nt+nt/2, nt / 2 - 1).first;
						bool rflag = false;
						if(L+2*nt>R){
							const int l3 = L + nt;
							int r3 = L + nt + nt / 2 - 1;
							const int l4 = L + nt + nt / 2;
							int r4 = L + nt + nt - 1;
							if (L + nt + nt / 2 > R) {
								rflag = true;
							}
							else {
								if (r4 > R) {
									r4 = R;
									assert(l4 <= R);
									r3 = l3 + (r4 - l4);
								}
								if (r4 < L+nt+p)assert(false);
								if (r3 < L + nt + p&&L + nt + p < l4)rflag = true;
							}

						}
						if (rflag) {
							if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else if(rsame){
								changeind = L + p;
								changeto = st[L + nt + p];
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else {
								ok = false;
								break;
							}
						}
						else {
							if (lsame&&rsame)assert(false);
							else if (lsame) {
								changeind = L + nt + p;
								changeto = st[L + p];
								assert(issame(L + nt, L + nt + nt / 2, nt / 2 - 1).first);
							}
							else if (rsame) {
								changeind = L + p;
								changeto = st[L + nt + p];

							}
							else {
								ok = false;
								break;
							}
						}
					}
				}
			}
		}
		if (ok)cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

