void cal_hash(){
  ull h=0;
  for(int i=0;i<s.size();i++){
    h=h*M+s[i];
    H[i+1]=h;
  }
  h=1;
  for(int i=0;i<N;i++){
    D[i]=h;
    h*=M;
  }
  s='X'+s;
}

int get_first_diff_idx(int l, int r, int t){

  int L=0, R=r-l-t+2;

  while(L<R){

    int Mid=(L+R)/2;
    ull v1=H[l+Mid]-H[l-1]*D[Mid+1];
    ull v2=H[l+Mid+t]-H[l+t-1]*D[Mid+1];

    if(v1==v2) L=Mid+1;
    else R=Mid;
  }

  return L;
}

bool isSame(int l, int r, int t, int A, int B){

  int i1=l+t, i2=r;
  ull h1, h2;

  if(i1<=A&&A<=i2){

    if(i1<A&&A<i2) h1=H[i2]-H[A]*D[i2-A]+((H[A-1]-H[i1-1]*D[A-i1])*D[1]+s[B])*D[i2-A];
    else if(A==i1) h1=H[i2]-H[i1]*D[i2-i1]+s[B]*D[i2-i1];
    else if(A==i2) h1=s[B]+(H[i2-1]-H[i1-1]*D[i2-i1])*D[1];

  }else h1=H[i2]-H[i1-1]*D[i2-i1+1];

  i1=l, i2=r-t;

  if(i1<=A&&A<=i2){

    if(i1<A&&A<i2) h2=H[i2]-H[A]*D[i2-A]+((H[A-1]-H[i1-1]*D[A-i1])*D[1]+s[B])*D[i2-A];
    else if(A==i1) h2=H[i2]-H[i1]*D[i2-i1]+s[B]*D[i2-i1];
    else if(A==i2) h2=s[B]+(H[i2-1]-H[i1-1]*D[i2-i1])*D[1];

  }else h2=H[i2]-H[i1-1]*D[i2-i1+1];
  cout<<h1<<' '<<h2<<endl;
  return h1==h2;
}

