    while(Q--){
        int l,r,t;
        scanf(" %d %d %d", &l, &r, &t);

        vector<int> check;
        // prefix
        int L = l, R = r;
        int ok = L+t-1, ng = R+1;
        rep(i,2){
            // printf(" binary s %d %d \n",ok,ng);
            // dbg(L);
            while(ng-ok>1){
                int mid = (ok+ng)/2;
                if(H.match(L+t,mid,L,mid-t)) ok = mid;
                else ng = mid;
            }
            if(l<=ok+1 && ok+1<=r) check.pb(ok+1);
            // dbg(ok+1);
            L = ok+2-t;
            ok = L+t-1;
            ng = R+1;
        }

        // suffix
        int cv_l = S-r+1, cv_r = S-l+1;
        L = cv_l;
        R = cv_r;
        ok = L+t-1;
        ng = R+1;
        rep(i,2){
            // printf(" binary s %d %d \n",ok,ng);
            // dbg(L);
            while(ng-ok>1){
                int mid = (ok+ng)/2;
                if(T.match(L+t,mid,L,mid-t)) ok = mid;
                else ng = mid;
            }
            if(l<=S+1 - (ok+1) && S+1 - (ok+1)<=r) check.pb(S+1 - (ok+1));
            // dbg(ok+1);
