    int sz = s.size();
    hashed.resize(sz+1, 0);
    power.resize(sz+1, 0);
    power[0] = 1;
    for(int i = 0; i < sz; i++) {
      power[i+1] = power[i]*base%mod;
      hashed[i+1] = (hashed[i]*base%mod+s[i])%mod;
