        int l, r, t;
        (cin >> l >> r >> t), l--;
        const int L = r - l;
        int inf = 0, sup = L - t;
        while (sup - inf > 1) {
            const int mid = (inf + sup) / 2;
            if (getHash(l, mid) == getHash(l + t, mid)) {
                inf = mid;
            } else {
                sup = mid;
            }
        }
        bool ok = false;
        for (int i = 0; i < 26; i++) {
            for (int j = 0; j < 2; j++) {
                const int pos = j == 0 ? inf : inf + t;
                const P ch = sub({i, i}, {S[pos + l] - 'a', S[pos + l] - 'a'});
                const P off1 = (pos < L - t ? power[pos] : P{0, 0});
                const P off2 = (pos >= t ? power[pos - t] : P{0, 0});
                if (add(getHash(l, L - t), mul(off1, ch)) == add(getHash(l + t, L - t), mul(off2, ch))) {
                    ok = true;
                    break;
                }
            }
        }
        cout << (ok ? "Yes\n" : "No\n");
    }
}

#include<bits/stdc++.h>
using namespace std;
typedef unsigned long long ull;
#define MAX_N 100005

ull B=5575777;
ull mem[MAX_N];

ull pow(int x){
  assert(0<=x && x<MAX_N);

  if(x==0)return 1;
  if(mem[x]>0)return mem[x];
  if(x&1)return mem[x]=pow(x-1)*B;
  mem[x]=pow(x/2);
  mem[x]*=mem[x];
  return mem[x];
}

int n,q,l,r,t;
char s[MAX_N];
ull p[MAX_N];

ull get(int l,int r){
  if(l==r)return 0;

  return p[r]-p[l]*pow(r-l);
}

ull get(int l,int r,int x,char ch){
  if(l==r)return 0;

  ull res=p[r]-p[l]*pow(r-l);
  if(l<=x&&x<r){
    int dist=r-x-1;
    res-=pow(dist)*s[x];
    res+=pow(dist)*ch;
  }
  return res;
}

bool eq(int a,int b,int l,int r){
  assert(b-a==r-l);
  return ( get(a,b) == get(l,r) );
}

bool eq(int a,int b,int l,int r, int x,char ch){
  assert(b-a==r-l);

  return ( get(a,b,x,ch) == get(l,r,x,ch) );
}

bool calc(int a,int b,int l,int r){
  assert(b-a==r-l);

  if(b-a!=r-l)return false;
  int L=0,R=b-a+1,M;
  while(R-L>1){
    M=(L+R)/2;
    if( eq(a,a+M,l,l+M) ){
      L=M;
    }else{
      R=M;
    }
  }
  if(L==b-a)return true;
  return ( eq(a,b,l,r, l+L, s[a+L])  );
  //  return ( eq(a,b,l,r, l+L, s[a+L]) || eq(a,b,l,r, a+L, s[l+L] )  );
}

bool solve(){
  int len=r-l;
  if(t==len)return true;
  if(t*2>=len){
    return calc(l,l+(len-t),r-(len-t),r);
  }
  if(len%t==0){
    return calc(l,r-t,l+t,r);
  }
  int as=len%t;

  //  calc(l+as,r-t,l+as+t,r);

  //  if ( calc(l+as,r-t ,l+as+t,r) && ( eq(l,l+as,r-t,r-t+as) || eq(l,l+as,r-t-t,r-t-t+as) ) )return true;
  //  if ( eq(l+as,r-t ,l+as+t,r) && ( calc(l,l+as,r-t,r-t+as) || calc(l,l+as,r-t-t,r-t-t+as) ) )return true;

  if ( calc(l,r-as-t ,l+t,r-as) && eq(l,l+as,r-as,r)  )return true;
  if ( calc(l+t,r-as ,l,r-as-t) && eq(l+t,l+t+as,r-as,r)  )return true;
  if ( eq(l,r-as-t ,l+t,r-as) && calc(l,l+as,r-as,r)  )return true;

  return false;
}

