constexpr ll BASE1 = 186398169LL;
constexpr ll MOD1 = 1000000007LL;
constexpr ll INV1 = inv(BASE1, MOD1);
constexpr ll BASE2 = 238173201LL;
constexpr ll MOD2 = 1000000009LL;
constexpr ll INV2 = inv(BASE2, MOD2);
constexpr int MAX = 100000;
P power[MAX], inverse[MAX], h[MAX];
map<P, int> phash;
int main()
{
    string S;
    cin >> S;
    for (ll i = 0, p1 = 1, p2 = 1, i1 = 1, i2 = 1, h1 = 0, h2 = 0; i < S.size(); i++, (p1 *= BASE1) %= MOD1, (i1 *= INV1) %= MOD1, (p2 *= BASE2) %= MOD2, (i2 *= INV2) %= MOD2) {
        (h1 += (S[i] - 'a') * p1 % MOD1) %= MOD1, (h2 += (S[i] - 'a') * p2 % MOD2) %= MOD2;
        power[i] = {p1, p2}, inverse[i] = {i1, i2}, h[i] = {h1, h2};
        for (int j = 1; j < 26; j++) { phash[{p1 * j % MOD1, p2 * j % MOD2}] = i; }
    };
    auto add = [](const P& p1, const P& p2) -> P { return {(p1.first + p2.first) % MOD1, (p1.second + p2.second) % MOD2}; };
    auto sub = [](const P& p1, const P& p2) -> P { return {(p1.first + MOD1 - p2.first) % MOD1, (p1.second + MOD2 - p2.second) % MOD2}; };
    auto mul = [](const P& p1, const P& p2) -> P { return {(p1.first * p2.first) % MOD1, (p1.second * p2.second) % MOD2}; };
    auto getHash = [&](const int s, const int l) -> P { return l == 0 ? P{0, 0} : s == 0 ? h[s + l - 1] : mul(sub(h[s + l - 1], h[s - 1]), inverse[s]); };
    auto getPoly = [&](const int t, const int rep) -> P { return {(po(power[t].first, rep, MOD1) + MOD1 - 1) * inv(power[t].first + MOD1 - 1, MOD1) % MOD1, (po(power[t].second, rep, MOD2) + MOD2 - 1) * inv(power[t].second + MOD2 - 1, MOD2) % MOD2}; };
    auto judge = [&](const P& diff, const int L) -> bool { return diff == P{0, 0} or (phash.find(diff) != phash.end() and phash[diff] < L) or (phash.find({(MOD1 - diff.first) % MOD1, (MOD2 - diff.second) % MOD2}) != phash.end() and phash[{-diff.first, -diff.second}] < L); };
    assert(phash.size() == 25 * S.size());
    int Q;
    cin >> Q;
    for (int q = 0; q < Q; q++) {
        int l, r, t;
        (cin >> l >> r >> t), l--;
        const int L = r - l, rep = L / t, rest = L % t;
        const P Hash = getHash(l, L);
        if (rep == 1) {
            const P mask1 = add(getHash(l, t), mul(power[t * rep], getHash(l + t * rep, rest)));
            const P diff = sub(mask1, Hash);
            cout << (judge(diff, L) ? "Yes" : "No") << endl;
        } else {
            const P poly = getPoly(t, rep);
            const P m1 = getHash(l, t);
            const P mask1 = add(mul(poly, m1), mul(power[t * rep], getHash(l + t * rep, rest)));
            const P diff1 = sub(mask1, Hash);
            const P m2 = getHash(l + t, t);
            const P mask2 = add(mul(poly, m2), mul(power[t * rep], getHash(l + t * rep, rest)));
            const P diff2 = sub(mask2, Hash);
            cout << (judge(diff1, L) or judge(diff2, L) ? "Yes" : "No") << endl;
        }
    }
}

