struct SuffixArray{
	vector<int> SA;
	vector<int>GYA;//SA
	vector<int>LCP;
	seg ki;
	string s;
	void Build_SA(const string& str){
		s = str;
		SA.resize(s.size());
		iota(begin(SA), end(SA), 0);
		sort(begin(SA), end(SA), [&](const int& a, const int& b)
		{
			if(s[a] == s[b]) {return(a > b);}
			return (s[a] < s[b]);
		});
		vector< int > classes(s.size()), c(s.size()), cnt(s.size());
		for(int i = 0; i < s.size(); i++) {c[i] = s[i];}
		for(int len = 1; len < s.size(); len <<= 1) {
			for(int i = 0; i < s.size(); i++) {
				if(i > 0 && c[SA[i - 1]] == c[SA[i]] && SA[i - 1] + len < s.size() && c[SA[i - 1] + len / 2] == c[SA[i] + len / 2]) {
					classes[SA[i]] = classes[SA[i - 1]];
				} else {
					classes[SA[i]] = i;
				}
			}
			iota(begin(cnt), end(cnt), 0);
			copy(begin(SA), end(SA), begin(c));
			for(int i = 0; i < s.size(); i++) {
				int s1 = c[i] - len;
				if(s1 >= 0) SA[cnt[classes[s1]]++] = s1;
			}
			classes.swap(c);
		}
		int ss=s.size();
		SA.insert(SA.begin(),ss);
	}
	int operator[](int k) const{return (SA[k]);}
	int size() const{return (s.size()+1);}
	bool lt_substr(string& t, int si = 0, int ti = 0)
	{
		int sn = s.size(), tn = t.size();
		while(si < sn && ti < tn) {
			if(s[si] < t[ti]) return (true);
			if(s[si] > t[ti]) return (false);
			++si, ++ti;
		}
		return (si >= sn && ti < tn);
	}
	int lower_bound(string& t)
	{
		int low = -1, high = SA.size();
		while(high - low > 1) {
			int mid = (low + high) >> 1;
			if(lt_substr(t, SA[mid])) low = mid;
			else high = mid;
		}
		return (high);
	}
	pair< int, int > lower_upper_bound(string& t)
	{
		int idx = lower_bound(t);
		int low = idx - 1, high = SA.size();
		t.back()++;
		while(high - low > 1) {
			int mid = (low + high) >> 1;
			if(lt_substr(t, SA[mid])) low = mid;
			else high = mid;
		}
		t.back()--;
		return (make_pair(idx, high));
	}
	void output()
	{
		for(int i = 0; i < size(); i++) {
			cout << i << ": " << s.substr(SA[i]) <<" "<<LCP[i]<< endl;
		}
	}
	void Build_LCP(void){
		GYA.resize(s.size()+1);
		LCP.resize(s.size()+1);
		int n=s.size(),i;
		for(i=0;i<=n;i++){GYA[SA[i]]=i;}
		int h=0;LCP[0]=0;
		for(i=0;i<n;i++){
			int j=SA[GYA[i]-1];
			if(h>0){h--;}
			for(;j+h<n&&i+h<n;h++){
				if(s[j+h]!=s[i+h]){break;}
			}
			LCP[GYA[i]-1]=h;
			ki.cha(GYA[i],h);
		}
	}
	int LCP_mozi(int l,int r){//?
		l=GYA[l];r=GYA[r];
		if(l>r){swap(l,r);}
		return ki.ask(l,r+1);
	}
};
int main(void){
	cout<<fixed<<setprecision(20);cin.tie(0);ios::sync_with_stdio(false);
	string s;cin>>s;s.pub('#');
	SuffixArray sa;
	sa.Build_SA(s);

	sa.Build_LCP();
	//sa.output();
	int Q;cin>>Q;
	while(Q--){
		int l,r,t,T;cin>>l>>r>>t;l--;
		T=t;
		while(T*2<r-l){T*=2;}
		int kai=0,hen=0;
		bool can=true;
		while(T>=t){
			//cerr<<"T="<<T<<endl;
			int iti=sa.LCP_mozi(l,l+T);
			//cerr<<"iti="<<iti<<endl;
			if(kai==0){
				if(iti<r-l-T){
					hen=1;
					int bas=l+iti;//
					if(sa.LCP_mozi(bas+1,bas+1+T)+iti+1<r-l-T){can=false;break;}
					int aaa=(iti>=T/2?iti-T/2:iti+T/2);
					if(s[l+iti]!=s[l+aaa]){l=r-T;}
				}
			}
			else if(kai==1){
				if(iti<T){
					if(hen>0){can=false;break;}
					int bas=l+iti;//
					if(sa.LCP_mozi(bas+1,bas+1+T)+iti+1<T){can=false;break;}//2
					int zyu=r-l-T-T;//
					int aaa=(iti>=T/2?iti-T/2:iti+T/2);
					//cerr<<"aaa="<<aaa<<endl;
					if(iti+T<zyu){can=false;break;}
					else if(iti>=zyu&&s[l+iti]!=s[l+aaa]){l+=T;}
				}
			}else{
				if(iti<T){can=false;break;}
			}
			kai++;T/=2;
		}
		cout<<(can?"Yes":"No")<<endl;
	}
	return 0;
}

