	int len = r - l;
	int Q = len / t;
	int R = len % t;
	int xlen = t;
	Hash x = substr(l+t,l+2*t);
	Hash res = 0;
	for(int i = 0 ; i < 17 ; i++){
		if( Q >> i & 1 ){
			res = concat(res,x,xlen);
		}

		x = concat(x,x,xlen);
		xlen *= 2;
	}

	res = concat(res,substr(l,l+R),R);
