			dis.push_back(P(Pow(b1, i) * (mod + j) % mod, Pow(b2, i) * (mod + j) % mod));
		}
	}
	sort(dis.begin(), dis.end());
	for (int i = 0, l, r, t; i < Q; i++) {
		cin >> l >> r >> t; l--; r--;
		auto lrh = rh.get(l, r - t), rrh = rh.get(l + t, r);
		ll d1 = (lrh.first - rrh.first + mod) % mod, d2 = (lrh.second - rrh.second + mod) % mod;
		if (r - l + 1 > t * 2) {
			if (binary_search(dis.begin(), dis.end(), P(d1, d2))) {
				puts("Yes");
			}
			else {
				int w = r - l + 1 , fl = (r - l + 1) / t * t;
				if (w == fl && rh.get(l, l + t - 1) != rh.get(r - t + 1, r)) {
					puts("No");
					continue;
				}
				if (w != fl && (rh.get(l, l + w - fl - 1) != rh.get(r - (w - fl - 1), r) || rh.get(l + w - fl, l + t - 1) != rh.get(r - t + 1, r - (w - fl)))) {
					puts("No");
					continue;
				}
				ll lb = t - 1, ub = r - l + 1 - t;
				while (lb + 1 < ub) {
					ll c = (lb + ub) / 2;
					lrh = rh.get(l, l + c - 1), rrh = rh.get(l + t, l + t + c - 1);
					d1 = (lrh.first - rrh.first + mod) % mod, d2 = (lrh.second - rrh.second + mod) % mod;
					if (binary_search(dis.begin(), dis.end(), P(d1, d2))) {
						lb = c;
					}
					else {
						ub = c;
					}
				}
				ll tmp = lb;
				lb = t - 1, ub = r - l + 1 - t;
				while (lb + 1 < ub) {
					ll c = (lb + ub) / 2;
					lrh = rh.get(r - t - c + 1, r - t), rrh = rh.get(r - c + 1, r);
					d1 = (lrh.first - rrh.first + mod) % mod, d2 = (lrh.second - rrh.second + mod) % mod;
					if (binary_search(dis.begin(), dis.end(), P(d1, d2))) {
						lb = c;
					}
					else {
						ub = c;
					}
				}
				puts(tmp + lb >= r - l ? "Yes" : "No");
			}
		}
		else {
			puts(binary_search(dis.begin(), dis.end(), P(d1, d2)) ? "Yes" : "No");
		}
	}
	return 0;
}

