#include <bits/stdc++.h>

using namespace std;

#define rep(i,n) for(int i=0; i<n; i++)

typedef long long ll;


template<class T>
struct RollingHash {
	static const int NN = 2; // the number of hash table
	T base[NN] = {1009, 1007};
	T PMOD[NN] = {1000000007, 1000000009};
	vector<T> hash[NN];
	vector<T> mypow[NN];
	int sz;
	void init(string s) {
		sz = s.size();
		for(int i = 0; i < NN; i++) {
			hash[i].assign(sz + 1, 0);
			mypow[i].assign(sz + 1, 0);
			mypow[i][0] = 1;
		}
		for(int i = 0; i < NN; i++) {
			for(int j = 0; j < sz; j++) {
				hash[i][j + 1] = (hash[i][j] * base[i] + s[j]) % PMOD[i];
				mypow[i][j + 1] = mypow[i][j] * base[i] % PMOD[i];
			}
		}
	}
	RollingHash() {
	}
	// hash[t] value of s[l..r]
	T get(int l, int r, int t) {
		T ret = (hash[t][r + 1] - hash[t][l] * mypow[t][r - l + 1] % PMOD[t] + PMOD[t]) % PMOD[t];
		return ret;
	}
	// compare s[a..b] and s[c..d] O(1)
	bool comp(int a, int b, int c, int d) {
		if(a < 0 || sz <= b || c < 0 || sz <= d) return false;
		for(int i = 0; i < NN; i++) {
			if(get(a, b, i) != get(c, d, i)) return false;
		}
		return true;
	}
	// longest common extension s[l..LCE(l,r)] == s[r..LCE(l,r)] && s[l..LCE(l,r)+1] != s[r..LCE(l,r)+1]
	int LCE(int l,int r){
		tie(l,r)=minmax(l,r);
		if(l==r) return sz-r+1;
		int ll = 0;
		int rr = sz;
		while(rr-ll>1){
			int mid = (ll+rr)>>1;
			if(comp(l,l+mid-1,r,r+mid-1)) ll=mid;
			else rr=mid;
		}
		return ll;
	}
};


int main(){
	string s;
	cin>>s;
	int Q;
	cin>>Q;
	RollingHash<ll> rh;
	rh.init(s);
	rep(_,Q){
		int l, r, t;
		cin>>l>>r>>t;
		l--;
		r--;
		bool ok = false;
		if(r-l+1==t){
			cout<<"Yes"<<endl;
			continue;
		}

		int a = rh.LCE(l,l+t);
		if(l+a>=r+1-t){
			cout<<"Yes"<<endl;
			continue;
		}
		int nl = l+a+1;
		a = rh.LCE(nl, nl+t);
		if(nl+a>=r+1-t){
			cout<<"Yes"<<endl;
			continue;
		}
		if(a==t-1){
			if(s[nl-1]==s[nl+2*t-1]){
				nl += a+1;
				a = rh.LCE(nl, nl+t);
				if(nl+a>=r+1-t){
					cout<<"Yes"<<endl;
					continue;
				}
			}
		}
		cout<<"No"<<endl;
	}
}

